style.desktop:
/* Hide anime spoilers */
/* Hide thumbnail */
[data-episode-grid-item]:not([data-is-watched="true"]) [data-episode-grid-item-image],
[data-episode-card-image] {
	filter: blur(12px);
}

/* Hide description, title, filename */
[data-episode-grid-item]:not([data-is-watched="true"]) [data-episode-grid-item-episode-title],
[data-episode-grid-item]:not([data-is-watched="true"]) [data-episode-grid-item-episode-description],
[data-episode-grid-item]:not([data-is-watched="true"]) [data-episode-grid-item-filename],
[data-undownloaded-episode-list="true"] [data-episode-grid-item-image-background],
[data-undownloaded-episode-list="true"] [data-episode-grid-item-episode-description],
[data-undownloaded-episode-list="true"] [data-episode-grid-item-episode-title] {
	filter: blur(3px);
	margin-left: -2px;
	mask-image: linear-gradient(90deg, transparent, black 2ch, black calc(100% - 3ch), transparent);
}

[data-episode-card-title="true"] {
	filter: blur(4px);
}

/* Only disable episode info button */
[data-episode-grid-item]:not([data-is-watched="true"]) [data-episode-grid-item-action] > [type="button"][aria-haspopup="dialog"] {
	opacity: 0.1;
	pointer-events: none;
}

body[data-scroll-locked] .scroll-locked-offset {
	padding-right: 0;
}

body[data-scroll-locked] [data-custom-library-banner-container] {
	width: 100vw !important;
}

[data-episode-card-image-container].rounded-xl {
	--border-radius-xl: 0.85rem;
	clip-path: inset(0 round var(--border-radius-xl));
}

/* Make Best Releases Stand Out */
[data-is-best-release="true"] {
	background-color: rgba(14,50,80,0.2) !important;
	box-shadow: 0 0 3px 1px #247fcc;
}

[data-media-entry-characters-section-grid-item-image-container] {
	transition: 0.4s ease-in-out;
	&:hover {
		transform: scale(3);
		z-index: 99999 !important;
	}
	&:hover [data-media-entry-characters-section-grid-item-image-background="true"] {
		background: unset !important;
	}
	&:hover [data-media-entry-characters-section-grid-item-image] {
		object-fit: contain !important;
	}
}

/* Display only [Multi Sub | Dual Audio] Torrents */
[data-torrent-preview-item="true"]:not([data-subtitle*="Multi-Subs"]):not([data-subtitle*="Dual Audio"]) {
	display: none;
}

/*Seanime compact grid card (6) */
[data-episode-list-grid] {
	grid-template-columns: repeat(6, minmax(0, 1fr));
	display: grid !important;
}

[data-episode-grid-item-image-container] {
	border-radius: 4px;
	width: 100%;
}

[data-episode-grid-item-image-overlay] {
	box-shadow: inset 0 0 12px rgba(0, 0, 0, 0.5);
}

[data-episode-grid-item-content] {
	display: grid;
	position: absolute;
	bottom: 2px;
	right: 4px;
	text-shadow: 0px 0px 3px rgb(0,0,0), 0px 0px 1px rgb(255,255,255);
}

[data-episode-grid-item-episode-title] {
	font-size: 0.9rem !important;
	bottom: 2px;
	right: 4px;
	text-align: right;
	text-wrap: balance;
}

[data-episode-grid-item-title] {
	font-size: 0.9rem;
	bottom: 2px;
	right: 2px;
	text-align: right;
}

[data-episode-grid-item-episode-description],
[data-episode-grid-item-filename] {
	display: none !important;
}

